import styles from './App.css';
import React, {useState, useEffect } from "react";
import Unity, { UnityContext } from "react-unity-webgl";
import * as solanaWeb3 from '@solana/web3.js';
import parse from 'html-react-parser';
import background from "./background.png";
import ProgressBar from 'react-bootstrap/Button';
import reactDom from 'react-dom';
import axios from "axios";
import { Connection, clusterApiUrl, LAMPORTS_PER_SOL } from "@solana/web3.js";import { getParsedNftAccountsByOwner,isValidSolanaAddress, createConnectionConfig,} from "@nfteyez/sol-rayz";
import logo from './Logo.svg';
import './App.css';

const unityContext = new UnityContext({
  loaderUrl: "./Build/public.loader.js",
  dataUrl: "./Build/public.data",
  frameworkUrl: "./Build/public.framework.js",
  codeUrl: "./Build/public.wasm"
});


function App() {
  const [isLoaded, setIsLoaded] = useState(false);
  const [progression, setProgression] = useState(0);
  const [provider, setProvider] = useState("");
}


export default App;
//create a connection of devnet
const createConnection = () => {
    return new Connection(clusterApiUrl("devnet"));
};

//check solana on window. This is useful to fetch address of your wallet.
const getProvider = () => {
  if ("solana" in window) {
  const provider = window.solana;
  if (provider.isPhantom) {
    return provider;
   }
  }
};

//get NFT
const getAllNftData = async () => {
  try {
      const connect =    createConnectionConfig(clusterApiUrl("devnet"));
      const provider = getProvider();
      let ownerToken = provider.publicKey;
      const result = isValidSolanaAddress(ownerToken);
      console.log("result", result);
const nfts = await getParsedNftAccountsByOwner({
        publicAddress: ownerToken,
        connection: connect,
        serialization: true,
      });
      return nfts;
  } catch (error) {
    console.log(error);
  }
};

//Function to get all nft data
const getNftTokenData = async () => {
  try {
    let nftData = await getAllNftData();
    var data = Object.keys(nftData).map((key) => nftData[key]);                                                                    let arr = [];
    let n = data.length;
    for (let i = 0; i < n; i++) {
      console.log(data[i].data.uri);
      let val = await axios.get(data[i].data.uri);
      arr.push(val);
    }
    return arr;
  } catch (error) {
    console.log(error);
  }
};

const NFT = (props) => {
  const [nftData, setNftData] = useState([]);
  const [loading, setLoading] = useState(false);
  createConnection();
  getProvider();
  getAllNftData();
useEffect(() => {
    async function data() {
      let res = await getAllNftData();
      setNftData(res);
      setLoading(true);
    }
    data();
  }, []);
return (
    <>
          <section className="nft mt-2 my-5">
            <div className="container">
              <div className="row text-center">
                <div className="col-12">
                  <h4 className="title">NFT</h4>
                </div>
              </div>
              <div className="row  d-flex justify-content-center">
                {loading ? (
                  <>
                    {nftData &&
                      nftData.length > 0 &&
                      nftData.map((val, ind) => {
                        return (
                          <div className="col-4 mt-3" key={ind}>
                            <div className="cart text-center">
                              <div className="img mt-4 pt-3">
                                <img src={val.data.image} alt="loading..." />
                                <p className="mt-1">{val.data.name}</p>
                                <h6 className=" mt-2">
                                  {val.data.description}
                                </h6>
                              </div>
                            </div>
                          </div>
                        );
                      })}
                  </>
                ) : (
                  <>
                    <p className="text-center">loading...</p>
                  </>
                )}
              </div>
            </div>
          </section>
    </>
  );
};


