import styles from './App.css';
import React, {useState, useEffect } from "react";
import Unity, { UnityContext } from "react-unity-webgl";
import * as solanaWeb3 from '@solana/web3.js';
import parse from 'html-react-parser';
import background from "./background.png";
import ProgressBar from 'react-bootstrap/Button';
import reactDom from 'react-dom';
import axios from "axios";
import { Connection, clusterApiUrl, LAMPORTS_PER_SOL } from "@solana/web3.js";
import { getParsedNftAccountsByOwner,isValidSolanaAddress, createConnectionConfig,} from "@nfteyez/sol-rayz";
import './App.css';

var publicKey;
var allNftData;

const unityContext = new UnityContext({
  loaderUrl: "./Build/public.loader.js",
  dataUrl: "./Build/public.data",
  frameworkUrl: "./Build/public.framework.js",
  codeUrl: "./Build/public.wasm"
});

function App() {
  const [isLoaded, setIsLoaded] = useState(false);
  const [progression, setProgression] = useState(0);
  const [provider, setProvider] = useState("");


  const getProvider = async () => {
    if ("solana" in window) {
      await window.solana.connect(); // opens wallet to connect to
      const provider = window.solana;
      if (provider.isPhantom) {
        console.log("Is Phantom installed?  ", provider.isPhantom);
        console.log("Are we connected?  ", provider.isConnected);
        console.log("And our key?  ", provider.publicKey.toString());
        return provider;
      }
    } else {
      window.alert('Install https://www.phantom.app/');
    }
  };

  window.onload = () => {
    getProvider().then(provider => {
      console.log('key', provider.publicKey.toString());
      publicKey = provider.publicKey.toString();
      setProvider(provider.publicKey.toString());
    }).then(() => {
      letsgo();
    }).catch(function(error){
      console.log(error)
    });
  }

  async function letsgo() {
     createConnection();
     getProvider();
       async function data() {
         let res = await getAllNftData();
         getNftTokenData();
       }
       data();
  }

  //create a connection
const createConnection = () => {
  return new Connection(clusterApiUrl("devnet"));
  // return new Connection(clusterApiUrl("mainnet-beta"));
};

  //get NFT
  const getAllNftData = async () => {
    try {
      // const connect =  createConnectionConfig(clusterApiUrl("mainnet-beta"));
      const connect =  createConnectionConfig(clusterApiUrl("devnet"));
      const provider = getProvider();
      let ownerToken = publicKey;
      const result = isValidSolanaAddress(ownerToken);
      const nfts = await getParsedNftAccountsByOwner({
        publicAddress: ownerToken,
        connection: connect,
        serialization: true,
      });
      return nfts;
  } catch (error) {
    console.log(error);
  }
};

//Function to get all nft data
const getNftTokenData = async () => {
  try {
    let nftData = await getAllNftData();
    var data = Object.keys(nftData).map((key) => nftData[key]);                                                                    
    let arr = [];
    let n = data.length;
    for (let i = 0; i < n; i++) {
      let val = await axios.get(data[i].data.uri);
      arr.push(val);
      console.log(val.data.image);
    }
    allNftData = arr;
    console.log(allNftData);
    return arr;
  } catch (error) {
    console.log(error);
  }
};

const NFT = async (props) => {
  const [nftData, setNftData] = useState([]);
  const [loading, setLoading] = useState(false);
  createConnection();
  getProvider();
useEffect(() => {
    async function data() {
      let res = await getAllNftData();
      setNftData(res);
      setLoading(true);
      getNftTokenData();
    }
    data();
  }, []);
}

  useEffect( () => {
   setProvider(publicKey);
  }, []);
  useEffect( () => {
    setProvider(publicKey);
   }, []);

   useEffect(function () {    
    unityContext.on("NFT", function () {
      if (window.solana.isConnected) {
        console.log(allNftData);
        unityContext.send("Wallet", "SetNFTData", allNftData);
      } else {
        window.alert("Please Connect your Wallet!");
      }
    });
  }, []);

  useEffect(function () {    
    unityContext.on("ConnectWallet", function () {
      getProvider().then(provider => {
        console.log('key', provider.publicKey.toString());
        publicKey = provider.publicKey.toString();
        setProvider(provider.publicKey.toString());
      }).then(() => {
        letsgo();
      }).catch(function(error){
        console.log(error)
      });
    });
  }, []);


  useEffect(function () {
    unityContext.on("progress", function (progression) {
      setProgression(progression);
    });
  }, []);


  useEffect(function () {
    unityContext.on("loaded", function () {
      setIsLoaded(true);
    });
  }, []);

  return (

  <div style = {{
    visibility: isLoaded ? "hidden" : "visible",
    backgroundImage: `url(${background})`,
      backgroundSize: "cover",
      height: "100%",
      width: "100%",
      color: "#f5f5f5",
    position: 'absolute',
    left: 0,
    top: 0,


  }}>
   <div>
  <ProgressBar variant="success" now={progression * 100}
  style={{ visibility: isLoaded ? "hidden" : "visible",
  height: "10px",
  width: "100%",
  position: "absolute",
  bottom: 0,
  color: "red",
  }}

  />

    </div>

   <p
    style={{ visibility: isLoaded ? "hidden" : "visible",
    height: "30px",
    width: "100%",
    position: "absolute",
    textAlign: 'center',
    bottom: "100px",
    fontSize: "2em",
    fontColor: "red",



    }}>help me...</p>

   <div>
   <Unity
      style={{ visibility: isLoaded ? "visible" : "hidden",
        height: "100%",
        width: "100%",
        background: "grey",
        position: 'absolute',
        left: 0,
        top: 0,
        overflow: "hidden",

    }}
      unityContext={unityContext}
    />
    </div>
    </div>
  );

  }

export default App
